svg.script:
//Inicializo Variables
var Color=webMI.query["Color"];
var Color2=webMI.query["Color2"];
var Raiz_Ruta;
var Raiz_RutaR=webMI.query["base"]+".Parametros.Raiz_Ruta";

/*********Al cargar Pantalla**********/

webMI.addOnload(function(e) {

	//Leo Raiz Ruta del nodo
	webMI.data.read(Raiz_RutaR, function(e) {
	    Raiz_Ruta=e.value;		
	})	

	var id = "Condiciones";
	webMI.gfx.setFill(id, Color2);
	webMI.gfx.setFill("Alarmas", Color);
	webMI.gfx.setFill("Mantenimiento", Color);

	setTimeout(function() {
		//Abro Condiciones
		webMI.display.openDisplay(Raiz_Ruta+".Condiciones", webMI.query, "info_frame");
	},200);

});


/*********ConfiguraciÃ³n de Botones**********/

//Si presiono Condiciones
webMI.addEvent("click_area_condiciones", "click", function(e) {
	var id = "Condiciones";
	webMI.gfx.setFill(id, Color2);
	webMI.gfx.setFill("Alarmas", Color);
	webMI.gfx.setFill("Mantenimiento", Color);

	//Abro Condiciones
	webMI.display.openDisplay(Raiz_Ruta+".Condiciones", webMI.query, "info_frame");
});


//Si presiono Alarmas
webMI.addEvent("click_area_alarmas", "click", function(e) {
	var id = "Alarmas";
	webMI.gfx.setFill("Condiciones", Color);
	webMI.gfx.setFill(id, Color2);
	webMI.gfx.setFill("Mantenimiento", Color);

	//Abro Alarmas
	webMI.display.openDisplay(Raiz_Ruta+".Alarmas", webMI.query, "info_frame");
});



//Si presiono Mantenimiento
webMI.addEvent("click_area_mantenimiento", "click", function(e) {
	var id = "Mantenimiento";
	webMI.gfx.setFill("Condiciones", Color);
	webMI.gfx.setFill("Alarmas", Color);
	webMI.gfx.setFill(id, Color2);

	//Abro Mantenmiento
	webMI.display.openDisplay(Raiz_Ruta+".Configuracion", webMI.query, "info_frame");

});
