svg.script:
//External Variables
var name= webMI.query["Name"];
var Border_Color= webMI.query["Border_Color"];
var Border_Thickness= webMI.query["Border_Thickness"];
var Font_Size= webMI.query["Font_Size"];
var Font_Color= webMI.query["Font_Color"];
var Font= webMI.query["Font"];
var Text_Editable= webMI.query["Text_Editable"];

// Global variable to hold the value
let globalValue = '';
var initial_Size={};
var FileInput;

//Reference HTML elements
const elementDiv0=document.getElementById('id_1');
const elementDiv=document.getElementById('inputSquare');

//Initial conditions
webMI.addOnload(function() {

   //Get div size for reference
	initial_Size = elementDiv0.getBoundingClientRect();





	// Create file input
	const fileInput = top.document.createElement('input');
	fileInput.type = 'file';
	fileInput.id = 'filePicker';
	fileInput.style.display = 'none'; // Hide the file input

	// Create custom button
	const customButton = top.document.createElement('button');
	customButton.id = 'customButton';
	customButton.textContent = 'Choose File';
	customButton.style.width = elementDiv.style.width; // Set width based on elementDiv

	// Create text display div
	const textDisplay = top.document.createElement('div');
	textDisplay.id = 'textDisplay';
	textDisplay.textContent = 'No file chosen';
	textDisplay.style.width = elementDiv.style.width; // Set width based on elementDiv
	textDisplay.style.width = Number(initial_Size.width-9-Border_Thickness)+"px";
	textDisplay.style.height = Number(initial_Size.height-9-Border_Thickness)+"px";
	textDisplay.style.borderColor = Border_Color;
    textDisplay.style.borderWidth = Number(Border_Thickness)+"px";
    textDisplay.style.fontFamily = Font;
    textDisplay.style.fontSize = Number(Font_Size)+"px";
    textDisplay.style.color = Font_Color;


	// Append elements to the DOM
	elementDiv.appendChild(fileInput);
	elementDiv.appendChild(customButton);
	elementDiv.appendChild(textDisplay);

	// Event listeners
	customButton.addEventListener('click', function() {
		fileInput.click();
	});

	fileInput.addEventListener('change', function(event) {
		const fileName = event.target.files[0] ? event.target.files[0].name : 'No file chosen';
		textDisplay.textContent = fileName;
	});

});


//Write value to the element remotely
webMI.trigger.connect("write_value_"+name, function(e) {
		console.log("Writting: "+e.value.value);
		FileInput.value = e.value.value;

});
